import React, { useState } from 'react';
import _ from 'lodash';
import { Link } from 'react-router-dom';
import {
  Button,
  Row,
  Col,
  ListGroup,
  Image,
  Card,
  ListGroupItem,
} from 'react-bootstrap';
import PropTypes from 'prop-types';
import { Helmet } from 'react-helmet';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../components/Message';
import CheckoutSteps from '../components/CheckoutSteps';
import {
  selectShippingCartAdress,
  selectPaymentMethod,
  selectCartItemsData,
  selectCartItemsLoading,
  selectCartItemsError,
} from '../redux/cart/selectors';
import Cart from './Cart';

const PlaceOrder = (props) => {
  const shippingAddress = useSelector(selectShippingCartAdress) || {};
  const paymentMethodSelector = useSelector(selectPaymentMethod) || 'Paypal';
  const cartItems = useSelector(selectCartItemsData);
  console.log(cartItems);
  return (
    <>
      <Helmet>
        <title>Proshop</title>
        <meta name="description" content="Place Order page" />
      </Helmet>
      <CheckoutSteps step1 step2 step3 step4 />
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>Shipping</h2>
              <p>
                <strong>Adress:</strong>
                {shippingAddress.address}, {shippingAddress.city} -{' '}
                {shippingAddress.postalCode} {shippingAddress.country}
              </p>
            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Payment Method</h2>
              <p>
                <strong>Method: </strong>
                {paymentMethodSelector}
              </p>
            </ListGroup.Item>
            <ListGroup.Item>
              <h2>Order Items</h2>
              {_.isEmpty(cartItems) ? (
                <Message>Your cart is empty</Message>
              ) : (
                <ListGroup>
                  {cartItems.map((item) => (
                    <ListGroup.Item key={item.product}>
                      <Row>
                        <Col md={1}>
                          <Image
                            src={item.image}
                            alt={item.name}
                            fluid
                            rounded
                          />
                        </Col>
                        <Col>
                          {' '}
                          <Link to={`/product/${item.product}`}></Link>
                          {item.name}
                        </Col>
                        <Col md={4}>
                            {item.qty} x ${item.price} = ${}
                        </Col>
                      </Row>
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </ListGroup.Item>
          </ListGroup>
        </Col>
      </Row>
    </>
  );
};

PlaceOrder.propTypes = {};

export default PlaceOrder;
