import * as CONSTANTS from '../constants';

export const listProducts = () => ({
  type: CONSTANTS.PRODUCT_LIST_REQUEST,
});

export const listProductDetails = (id) => {
  return {
    type: CONSTANTS.PRODUCT_DETAILS_REQUEST,
    id,
  };
};
