import { takeEvery, put, all, call, select } from 'redux-saga/effects';
import * as api from './services';
import * as CONSTANTS from './constants';

export function* createOrderWatcher({ order }) {
  try {
    const data = yield call(api.createOrderService, id);
    const state = yield select();
    yield put({
      type: CONSTANTS.CREATE_ORDER_SUCCESS,
      payload: {
        product: data._id,
        name: data.name,
        image: data.image,
        price: data.price,
        countInStock: data.countInStock,
        qty,
      },
    });
    localStorage.setItem(
      'cartItems',
      JSON.stringify(state.cart.data.cartItems),
    );
  } catch (error) {
    yield put({
      type: CONSTANTS.CREATE_ORDER_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
}

export default function* productSaga() {
  yield all([createOrderWatcher()]);
}
