import * as CONSTANTS from './constants';

export const getUserDetails = (id) => ({
  type: CONSTANTS.USER_DETAILS_REQUEST,
  id,
});


export const updateUserDetails = (id) => ({
  type: CONSTANTS.USER_U,
  id,
});

export const resetUserDetails = (id) => ({
  type: CONSTANTS.USER_DETAILS_REQUEST,
  id,
});
