import * as CONSTANTS from './constants';

export const login = (payload) => ({
  type: CONSTANTS.USER_LOGIN_REQUEST,
  payload,
});


export const logout = (payload) => ({
  type: CONSTANTS.USER_LOGOUT_REQUEST,
  payload,
});
